import {useState} from "react"


function Todo (){
    const [todos,setTodos] = useState([]);
    const [task,setTask] = useState(""); 

    // const [task, setTask] = useState('')

    // task = ''
    // setTask = is a function to change what task is
    // setTask('john')  
    // task now becomes john aka task = 'john'

    // in react it has problems with variables mutating so you need to use useState hooks to change variables which trigger a re-render

    return (
        <>
        <h1 className="text-center uppercase text-8xl font-bold text-green-800">Todo List</h1>
        <div className='flex justify-center items-center mt-8'>


            <form className='flex space-x-4'>
                <input value={task} onChange={(e) => {
                    setTask(e.target.value)
                    //console.log(task)

                }} type="text" className="flex-1 border"/>
                <button onClick={ (e) => {
                    e.preventDefault()
                    setTodos(function(){
                        todos.concat(task)
                    })
                    console.log(todos)
                    }} type="submit" className='px-4 py-2 bg-green-600 text-center text-white uppercase'>Submit</button>
            </form>

        </div>
        </>
    )
};

export default Todo;
